$(document).ready(function(){$("#login").click(function(){var e=$("#username").val(),t=$("#password").val();e&&t?$.post("/login/validate",{username:e,password:t},function(e){console.log(e),alert(e)}).fail(function(e){console.log(e.responseText.message),alert(e.message),alert("Error: "+e.getResponseHeader("message"))}):alert("A username and password is required")})});